mapper.select:
SELECT id, name, title, price, stock, description, image_url, create_time, update_time
FROM goods
WHERE id = #{id};

mapper.update:
UPDATE goods
SET stock = #{stock}
WHERE id = #{id};

mapper.insert:
INSERT INTO `goods`(
    name, title, price, stock, description, image_url, create_time, update_time
) VALUES (
    #{name}, #{title}, #{price}, #{stock}, #{description}, #{image_url}, #{create_time}, #{update_time}
)
